AddNPC("Instance Test Guy","models/player.mdl") --add to rpg editorClickMenus["Instance Test Guy"] = function (menu,pl,ent) --right click menu	if pl:CanReach(ent) then		menu:AddOption("Talk To",function() RunConsoleCommand("talkto",ent:EntIndex()) end)	endend--Create a quest like thislocal instance_test = quest.new("Instance Test") --start a new questlocal t = {}t.Description = [[Go through the door and kill a zombie]]t.Kills = {}t.Kills["npc_zombie_headcrabless"] = 1instance_test:AddPart(t)hook.Add("CanPlayerUseDoor","Instance_Door_Test",function(pl,ent)	if ent:GetNWString("Name") == "dev_house" then		return pl:ActiveQuest(instance_test)	endend)instance_test.Rewards = {}Dialog["Instance Test Guy"] = {}Dialog["Instance Test Guy"].StartingPoint = function(pl)endDialog["Instance Test Guy"][1] = {	Text 		= "This is a test of the instancing system. Proceed through the door",	Replies 	= {1,2}}Replies["Instance Test Guy"] = {} Replies["Instance Test Guy"][1] = {	Text		= "Accept Quest",	OnUse		= function(pl) pl:AcquireQuest(instance_test) end}Replies["Instance Test Guy"][2] = {	Text		= "Deny Quest",	OnUse		= function(pl) end}